import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import "../CheckInfo.css";
import "../Calendar.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { ko } from "date-fns/esm/locale";
import { addDays, differenceInDays } from "date-fns";


function CheckInfo() {
  const navigate = useNavigate();

  // 선택된 정보 저장
  const [selectedData, setSelectedData] = useState({
    times: { start: "", end: "" },
    car: null,
    themes: [],
    location: null,
    with: null,
  });

  const handleSelect = (name, value) => {
    setSelectedData({
      ...selectedData,
      [name]: value,
    });
  };
  const handleDateSelect = ({ startDate, endDate }) => {
    handleSelect(startDate);
    handleSelect(endDate);
  };
  const handleTimeSelect = (e) => {
    handleSelect("times", {
      ...selectedData.times,
      [e.target.name]: e.target.value,
    });
  };

  const handleCarSelect = (index) => {
    handleSelect("car", index === selectedData.car ? selectedData.car : index);
  };

  const handleLocationSelect = (index) => {
    handleSelect("location", index === selectedData.location ? null : index);
  };

  const handleWithSelect = (index) => {
    handleSelect("with", index === selectedData.with ? null : index);
  };

  const handleThemeSelect = (theme) => {
    if (selectedData.themes.includes(theme)) {
      if (selectedData.themes.length > 1) {
        setSelectedData({
          ...selectedData,
          themes: selectedData.themes.filter((t) => t !== theme),
        });
      }
    } else if (selectedData.themes.length < 3) {
      setSelectedData({
        ...selectedData,
        themes: [...selectedData.themes, theme],
      });
    } else {
      alert("최대 3개까지만 선택 가능합니다!");
    }
  };

  // !확인용! 지울 것
  console.log(selectedData);
  const dataToSend = JSON.stringify(selectedData);
  console.log(dataToSend);
  // !확인용! 지울 것

  return (
    <div className="CheckInfo">
      <div id="title">
        <button id="back" onClick={() => navigate(-1)}>
          {`<`}
        </button>
        <text>이번 제주 여행은 ...</text>
      </div>
      <div className="Q">
        <text>1. 여행일자를 선택해주세요.</text>
        <Calendar onDateChange={handleDateSelect}/>
      </div>
      <div className="Q">
        <text>2. 여행 시작과 종료 시간을 선택해주세요.</text>
        <div className="CheckTime">
          <text>시작</text>
          <select
            name="start"
            value={selectedData.times.start}
            onChange={handleTimeSelect}
          >
            <option value={"아침"}>아침 (8-9시)</option>
            <option value={"점심"}>점심 (12-13시)</option>
            <option value={"오후"}>오후 (14-15시)</option>
          </select>
          <text>|</text>
          <text>종료</text>
          <select
            name="end"
            value={selectedData.times.end}
            onChange={handleTimeSelect}
          >
            <option value={"18-20시"}>18-20시</option>
            <option value={"20-21시"}>20-21시</option>
            <option value={"21-22시"}>21-22시</option>
          </select>
        </div>
      </div>
      <div className="Q">
        <text>3. 이동수단으로 차를 이용하실 건가요?</text>
        <span className="car">
          <button
            className={selectedData.car === "car" ? "selected" : ""}
            onClick={() => handleCarSelect("car")}
          >
            O
          </button>
          <button
            className={selectedData.car === "bus" ? "selected" : ""}
            onClick={() => handleCarSelect("bus")}
          >
            X
          </button>
        </span>
      </div>
      <div className="Q">
        <text>4. 여행 테마를 선택해주세요. (최대 3개 선택 가능)</text>
        <span className="theme">
          <button
            className={selectedData.themes.includes("힐링") ? "selected" : ""}
            onClick={() => handleThemeSelect("힐링")}
          >
            힐링
          </button>
          <button
            className={selectedData.themes.includes("자연") ? "selected" : ""}
            onClick={() => handleThemeSelect("자연")}
          >
            자연
          </button>
          <button
            className={selectedData.themes.includes("체험") ? "selected" : ""}
            onClick={() => handleThemeSelect("체험")}
          >
            체험
          </button>
          <br />
          <button
            className={selectedData.themes.includes("관광") ? "selected" : ""}
            onClick={() => handleThemeSelect("관광")}
          >
            관광
          </button>
          <button
            className={selectedData.themes.includes("맛집") ? "selected" : ""}
            onClick={() => handleThemeSelect("맛집")}
          >
            맛집
          </button>
          <button
            className={selectedData.themes.includes("카페") ? "selected" : ""}
            onClick={() => handleThemeSelect("카페")}
          >
            카페
          </button>
        </span>
      </div>
      <div className="Q">
        <text>5. 선호하는 여행 지역을 선택해주세요. (필수 X)</text>
        <span className="location">
          <button
            className={selectedData.location === "west" ? "selected" : ""}
            onClick={() => handleLocationSelect("west")}
          >
            서 부
          </button>
          <button
            className={selectedData.location === "east" ? "selected" : ""}
            onClick={() => handleLocationSelect("east")}
          >
            동 부
          </button>
        </span>
      </div>
      <div className="Q">
        <text>6. 동행자 유형을 선택해주세요. (필수 X)</text>
        <span className="with">
          <button
            className={selectedData.with === "parent" ? "selected" : ""}
            onClick={() => handleWithSelect("parent")}
          >
            부모님
          </button>
          <button
            className={selectedData.with === "child" ? "selected" : ""}
            onClick={() => handleWithSelect("child")}
          >
            아 이
          </button>
          <button
            className={selectedData.with === "couple" ? "selected" : ""}
            onClick={() => handleWithSelect("couple")}
          >
            연 인
          </button>
          <button
            className={selectedData.with === "friend" ? "selected" : ""}
            onClick={() => handleWithSelect("friend")}
          >
            친 구
          </button>
        </span>
        <button
          id="result"
          onClick={() => {
            navigate("options");
          }}
        >
          경로 추천 받기
        </button>
      </div>
    </div>
  );
}

export default CheckInfo;
