import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import "../CheckInfo.css";
import Calendar from "./Calendar";

function CheckInfo() {
  const navigate = useNavigate();

  // 정보 선택
  const [timesSelected, setTimesSelected] = useState({ start: "", end: "" });
  const [carSelected, setCarSelected] = useState(null);
  const [themeSelected, setThemeSelected] = useState([]);
  const [locationSelected, setLocationSelected] = useState(null);
  const [withSelected, setWithSelected] = useState(null);

  const handleTimeSelect = (e) => {
    setTimesSelected({ ...timesSelected, [e.target.name]: e.target.value });
  };
  const handleCarSelect = (index) => {
    setCarSelected(index === carSelected ? carSelected : index);
  };

  const handleLocationSelect = (index) => {
    setLocationSelected(index === locationSelected ? null : index);
  };

  const handleWithSelect = (index) => {
    setWithSelected(index === withSelected ? null : index);
  };

  const handleThemeSelect = (theme) => {
    if (themeSelected.includes(theme)) {
      if (themeSelected.length > 1)
        setThemeSelected(themeSelected.filter((t) => t !== theme));
    } else if (themeSelected.length < 3) {
      setThemeSelected([...themeSelected, theme]);
    } else {
      alert("최대 3개까지만 선택 가능합니다!");
    }
  };

  console.log(timesSelected,carSelected, themeSelected, locationSelected, withSelected);

  return (
    <div className="CheckInfo">
      <div id="title">
        <button id="back" onClick={() => navigate(-1)}>
          {`<`}
        </button>
        <text>예정된 여행에 대한 정보를 선택해주세요.</text>
      </div>
      <div className="Q">
        <text>1. 여행일자를 선택해주세요.</text>
        <Calendar />
      </div>
      <div className="Q">
        <text>2. 여행 시작과 종료 시간을 선택해주세요.</text>
        <div className="CheckTime">
          <text>시작</text>
          <select name="start" value={timesSelected.start} onChange={handleTimeSelect}>
            <option value={"아침"}>아침 (8-9시)</option>
            <option value={"점심"}>점심 (12-13시)</option>
            <option value={"오후"}>오후 (14-15시)</option>
          </select>
          <text>|</text>
          <text>종료</text>
          <select>
            <option value={"18-20시"}>18-20시</option>
            <option value={"20-21시"}>20-21시</option>
            <option value={"21-22시"}>21-22시</option>
          </select>
        </div>
        <div className="Q">
          <text>3. 이동수단으로 차를 이용하실 건가요?</text>
          <span className="car">
            <button
              className={carSelected === "car" ? "selected" : ""}
              onClick={() => handleCarSelect("car")}
            >
              O
            </button>
            <button
              className={carSelected === "bus" ? "selected" : ""}
              onClick={() => handleCarSelect("bus")}
            >
              X
            </button>
          </span>
          <div className="Q">
            <text>4. 여행 테마를 선택해주세요. (최대 3개 선택 가능)</text>
            <span className="theme">
              <button
                className={themeSelected.includes("힐링") ? "selected" : ""}
                onClick={() => handleThemeSelect("힐링")}
              >
                힐링
              </button>
              <button
                className={themeSelected.includes("자연") ? "selected" : ""}
                onClick={() => handleThemeSelect("자연")}
              >
                자연
              </button>
              <button
                className={themeSelected.includes("체험") ? "selected" : ""}
                onClick={() => handleThemeSelect("체험")}
              >
                체험
              </button>
              <br />
              <button
                className={themeSelected.includes("관광") ? "selected" : ""}
                onClick={() => handleThemeSelect("관광")}
              >
                관광
              </button>
              <button
                className={themeSelected.includes("맛집") ? "selected" : ""}
                onClick={() => handleThemeSelect("맛집")}
              >
                맛집
              </button>
              <button
                className={themeSelected.includes("카페") ? "selected" : ""}
                onClick={() => handleThemeSelect("카페")}
              >
                카페
              </button>
            </span>
          </div>
          <div className="Q">
            <text>5. 선호하는 여행 지역을 선택해주세요. (필수 X)</text>
            <span className="location">
              <button
                className={locationSelected === "west" ? "selected" : ""}
                onClick={() => handleLocationSelect("west")}
              >
                서 부
              </button>
              <button
                className={locationSelected === "east" ? "selected" : ""}
                onClick={() => handleLocationSelect("east")}
              >
                동 부
              </button>
            </span>
          </div>
          <div className="Q">
            <text>6. 동행자 유형을 선택해주세요. (필수 X)</text>
            <span className="with">
              <button
                onClick={() => handleWithSelect("parent")}
                className={withSelected === "parent" ? "selected" : ""}
              >
                부모님
              </button>
              <button
                onClick={() => handleWithSelect("child")}
                className={withSelected === "child" ? "selected" : ""}
              >
                아 이
              </button>
              <button
                onClick={() => handleWithSelect("couple")}
                className={withSelected === "couple" ? "selected" : ""}
              >
                연 인
              </button>
              <button
                onClick={() => handleWithSelect("friend")}
                className={withSelected === "friend" ? "selected" : ""}
              >
                친 구
              </button>
            </span>
          </div>
          <div>
            <button
              id="result"
              onClick={() => {
                navigate("options");
              }}
            >
              경로 추천 받기
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CheckInfo;
