import { useNavigate, useLocation } from "react-router-dom";
import "../DetailRoute.css";
import KakaoMap from "../components/KakaoMap";
import WordCloud from "react-d3-cloud";
import DayBtn from "../components/DayBtn";

function DetailRoute() {
  const navigate = useNavigate();
  const location = useLocation();
  const duration = location.state.duration;
  const selectedData = location.state.selectedData;
  //지울 것
  console.log(duration, selectedData);

  const data = [
    { test: duration, value: 1000 },
    { text: selectedData.location, value: 1000 },
    { text: selectedData.with, value: 1000 },
    ...selectedData.themes.map((theme) => ({ text: theme, value: 1000 })),
  ];

  return (
    <div className="DetailRoute">
      <div className="about">
        <button id="back" onClick={() => navigate(-1)}>
          {`<`}
        </button>
        <text>자세한 경로는 ... </text>
      </div>
      <div className="info">
        <div id="left">
          <div id="lTop">
            <DayBtn duration={duration} />
          </div>
          <div id="lMiddle">
            <text># 키워드</text>
            {/* <WordCloud
              data={data}
              width={150}
              height={100}
              padding={6}
              fontSize={13}
              rotate={(word) => Math.random() * 90 - 45}
              font="sunflowerL"
              fill={"#ffb349"}
              border={1}
            /> */}
            <svg>
              {data.map((word, index) => (
                <text
                  key={index}
                  x={Math.random() * 500} // x 좌표 설정
                  y={Math.random() * 200} // y 좌표 설정
                  fontSize={11} // 글꼴 크기 설정
                  rotate={(word) => Math.random() * 90 - 45}
                  fill="blue" // 텍스트 색상 설정
                >
                  {word.text}
                </text>
              ))}
            </svg>
          </div>
          <div id="lDown">
            <button></button>
          </div>
        </div>

        <div id="right">
          <KakaoMap />
        </div>
      </div>
    </div>
  );
}

export default DetailRoute;
