import React, { useEffect, useState, useRef } from "react";
import axios from "axios";

function Map(props) {
  const [mapData, setMapData] = useState("");
  const mapContainerRef = useRef(null);

  useEffect(() => {
    axios
      .get(`http://13.209.235.204:8000/map/${props.option}/${props.day}/`)
      .then((response) => {
        setMapData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching HTML data:", error);
      });
  }, [props.option, props.day]);

  useEffect(() => {
    if (mapData && mapContainerRef.current) {
      mapContainerRef.current.innerHTML = mapData;
    }
  }, [mapData]);

  return <div id="map" ref={mapContainerRef}></div>;
}

export default Map;
