import React, { useEffect } from "react";

const { kakao } = window;

function KakaoMap(props) {
  useEffect(() => {
    const container = document.getElementById("map");

    if (props.responseData && Array.isArray(props.responseData)) {
      const markerPositions = props.responseData.map((item, index) => {
        const latitude = item.latitude;
        const longitude = item.longitude;

        const marker = new kakao.maps.Marker({
          position: new kakao.maps.LatLng(latitude, longitude),
        });

        // 순서를 표시하는 라벨을 생성합니다.
        const label = document.createElement("div");
        label.className = "marker-label";
        label.innerText = String(index + 1);

        const customOverlay = new kakao.maps.CustomOverlay({
          content: label,
          position: new kakao.maps.LatLng(latitude, longitude),
          yAnchor: 1, // 라벨의 기준점을 아래쪽으로 설정합니다.
        });

        customOverlay.setMap(map); // 라벨을 지도에 추가합니다.
        return marker;
      });

      const bounds = new kakao.maps.LatLngBounds();
      markerPositions.forEach((marker) => {
        marker.setMap(map);
        bounds.extend(marker.getPosition());
      });

      const center = getCenterFromBounds(bounds);

      const options = {
        center: center,
        level: 10,
      };
      const map = new kakao.maps.Map(container, options);
    }
  }, [props.responseData]);

  function getCenterFromBounds(bounds) {
    const ne = bounds.getNorthEast();
    const sw = bounds.getSouthWest();
    const centerLat = (ne.getLat() + sw.getLat()) / 2;
    const centerLng = (ne.getLng() + sw.getLng()) / 2;
    return new kakao.maps.LatLng(centerLat, centerLng);
  }

  return <div id="map"></div>;
}

export default KakaoMap;
