import { useState, useMemo } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import WordCloud from "react-d3-cloud";
import KakaoMap from "../components/KakaoMap";
import DayBtn from "../components/DayBtn";
import Route from "../components/Route";
import "../DetailRoute.css";

function DetailRoute() {
  const navigate = useNavigate();
  const location = useLocation();
  const duration = location.state.duration;
  const selectedData = location.state.selectedData;
  const [selectedDay, setSelectedDay] = useState(1);

  let durationStr;
  if (duration === 1) {
    durationStr = "당일치기";
  } else if (duration === 2) {
    durationStr = "1박 2일";
  } else if (duration === 3) {
    durationStr = "2박 3일";
  }

  const data = useMemo(
    () => [
      { text: selectedData.startDate, value: 50, fill: "black" },
      { text: durationStr, value: 400, fill: "blue" },
      { text: selectedData.location, value: 300 },
      { text: selectedData.with, value: 600 },
      ...selectedData.themes.map((theme) => ({ text: theme, value: 3000 })),
    ],
    [
      selectedData.startDate,
      durationStr,
      selectedData.location,
      selectedData.with,
      selectedData.themes,
    ]
  );

  const handleDayClick = (day) => {
    setSelectedDay(day);
  };

  return (
    <div className="DetailRoute">
      <div className="about">
        <button id="back" onClick={() => navigate(-1)}>
          {`<`}
        </button>
        <text>자세한 경로는 ... </text>
      </div>
      <div className="info">
        <div id="left">
          <div id="lTop">
            <DayBtn duration={duration} onDayClick={handleDayClick} />
          </div>
          <div id="lMiddle">
            <text># 키워드</text>
            <div className="wordcloud">
              <MemoizedWordCloud
                data={data}
                width={200}
                height={120}
                padding={4}
                fontSize={(word) => Math.log2(word.value) * 1.7}
                rotate={0}
                font="sunflowerL"
                fill={"#FAAC58"}
              />
            </div>
          </div>
          <div id="lDown">
            <Route selectedDay={selectedDay} />
          </div>
        </div>

        <div id="right">
          <KakaoMap />
        </div>
      </div>
    </div>
  );
}

export default DetailRoute;
