import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = momentLocalizer(moment);

function App() {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  const handleSelect = ({ start, end }) => {
    if (end.diff(start, "days") <= 2) {
      setStartDate(start);
      setEndDate(end);
    }
  };

  return (
    <div>
      <div className="Q">
        <text>1. 여행일자를 선택해주세요.</text>
        <Calendar
          selectable
          localizer={localizer}
          events={[]}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 500 }}
          onSelectEvent={() => {}}
          onSelectSlot={handleSelect}
          min={new Date()}
          max={moment().add(1, "year").toDate()}
        />
      </div>
      <div>
        {startDate && endDate ? (
          <div>
            <text>여행일정: </text>
            <text>
              {moment(startDate).format("YYYY-MM-DD")} 부터{" "}
              {moment(endDate).format("YYYY-MM-DD")} 까지 ({endDate.diff(startDate, "days")}박{" "}
              {endDate.diff(startDate, "days") + 1}일)
            </text>
          </div>
        ) : (
          <text>여행일정을 선택해주세요.</text>
        )}
      </div>
    </div>
  );
}

export default App;
