import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../CheckInfo.css";
import "../fonts/fonts.css";

function CheckInfo() {
  const navigate = useNavigate();

  // ÏÑ†ÌÉùÎêú Ï†ïÎ≥¥ Ï†ÄÏû•
  //const [getInfo, setGetInfo] = useState("");

  // Ï∞® Ïú†Î¨¥ ÏÑ†ÌÉù
  const [carSelected, setCarSelected] = useState(null);

  const handleCarSelect = (index) => {
    setCarSelected(index === carSelected ? carSelected : index);
  };
  console.log(carSelected);

  // Ïó¨ÌñâÏßÄÏó≠ ÏÑ†ÌÉù
  const [locationSelected, setLocationSelected] = useState(null);

  const handleLocationSelect = (index) => {
    setLocationSelected(index === locationSelected ? null : index);
  };

  // ÎèôÌñâÏûê Ïú†Ìòï ÏÑ†ÌÉù
  const [withSelected, setWithSelected] = useState(null);

  const handleWithSelect = (index) => {
    setWithSelected(index === withSelected ? null : index);
  };

  const buttons = document.querySelectorAll(".theme button"); // Î™®Îì† Î≤ÑÌäº ÏÑ†ÌÉù
  const minSelect = 1; // ÏµúÏÜå ÏÑ†ÌÉù Í∞ÄÎä•Ìïú Í∞úÏàò
  const maxSelect = 3; // ÏµúÎåÄ ÏÑ†ÌÉù Í∞ÄÎä•Ìïú Í∞úÏàò
  let selectedCount = 0; // ÏÑ†ÌÉùÎêú Î≤ÑÌäº Í∞úÏàò Ï¥àÍ∏∞Ìôî

  // Í∞Å Î≤ÑÌäºÏóê ÌÅ¥Î¶≠ Ïù¥Î≤§Ìä∏ Ï∂îÍ∞Ä
  buttons.forEach((button) => {
    button.addEventListener("click", () => {
      if (
        selectedCount >= maxSelect &&
        !button.classList.contains("selected")
      ) {
        alert(`ÏµúÎåÄ ${maxSelect}Í∞úÍπåÏßÄÎßå ÏÑ†ÌÉùÌï† Ïàò ÏûàÏäµÎãàÎã§.`);
        button.disabled = true;
        return;
      }
      else{
        button.disabled=false;
      }
      if (!button.classList.contains("selected")) {
        selectedCount++;
        button.classList.add("selected"); // ÏÑ†ÌÉùÎêú Î≤ÑÌäºÏóê selected ÌÅ¥ÎûòÏä§ Ï∂îÍ∞Ä
      } else {
        selectedCount--;
        button.classList.remove("selected"); // ÏÑ†ÌÉù Ï∑®ÏÜåÎêú Î≤ÑÌäºÏùò selected ÌÅ¥ÎûòÏä§ Ï†úÍ±∞
      }
    });
  });

  return (
    <div className="CheckInfo">
      <div id="title">
        <text>ÏòàÏ†ïÎêú Ïó¨ÌñâÏóê ÎåÄÌïú Ï†ïÎ≥¥Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</text>
      </div>
      <div className="Q">
        <text>1. Ïó¨ÌñâÏùºÏûêÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</text>
        <button id="day">ÎÇ†Ïßú ÏÑ†ÌÉù üóìÔ∏è</button>
      </div>
      <div className="Q">
        <text>2. Ïó¨Ìñâ ÏãúÏûëÍ≥º Ï¢ÖÎ£å ÏãúÍ∞ÑÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</text>
        <div className="CheckTime">
          <text>ÏãúÏûë</text>
          <select>
            <option value={"ÏïÑÏπ®(8-9Ïãú)"}>ÏïÑÏπ® (8-9Ïãú)</option>
            <option value={"Ï†êÏã¨(12-13Ïãú)"}>Ï†êÏã¨ (12-13Ïãú)</option>
            <option value={"Ïò§ÌõÑ(14-15Ïãú)"}>Ïò§ÌõÑ (14-15Ïãú)</option>
          </select>
          <text>|</text>
          <text>Ï¢ÖÎ£å</text>
          <select>
            <option value={"18-20Ïãú"}>18-20Ïãú</option>
            <option value={"20-21Ïãú"}>20-21Ïãú</option>
            <option value={"21-22Ïãú"}>21-22Ïãú</option>
          </select>
        </div>
        <div className="Q">
          <text>3. Ïù¥ÎèôÏàòÎã®ÏúºÎ°ú Ï∞®Î•º Ïù¥Ïö©ÌïòÏã§ Í±¥Í∞ÄÏöî?</text>
          <span className="car">
            <button
              className={carSelected === 1 ? "selected" : ""}
              onClick={() => handleCarSelect(1)}
            >
              O
            </button>
            <button
              className={carSelected === 2 ? "selected" : ""}
              onClick={() => handleCarSelect(2)}
            >
              X
            </button>
          </span>
          <div className="Q">
            <text>4. Ïó¨Ìñâ ÌÖåÎßàÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî. (ÏµúÎåÄ 3Í∞ú ÏÑ†ÌÉù Í∞ÄÎä•)</text>
            <span className="theme">
              <button>ÌûêÎßÅ</button>
              <button>ÏûêÏó∞</button>
              <button>Ï≤¥Ìóò</button>
              <br />
              <button>Í¥ÄÍ¥ë</button>
              <button>ÎßõÏßë</button>
              <button>Ïπ¥Ìéò</button>
            </span>
          </div>
          <div className="Q">
            <text>5. ÏÑ†Ìò∏ÌïòÎäî Ïó¨Ìñâ ÏßÄÏó≠ÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî. (ÌïÑÏàò X)</text>
            <span className="location">
              <button
                className={locationSelected === 1 ? "selected" : ""}
                onClick={() => handleLocationSelect(1)}
              >
                Îèô Î∂Ä
              </button>
              <button
                className={locationSelected === 2 ? "selected" : ""}
                onClick={() => handleLocationSelect(2)}
              >
                ÏÑú Î∂Ä
              </button>
            </span>
          </div>
          <div className="Q">
            <text>6. ÎèôÌñâÏûê Ïú†ÌòïÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî. (ÌïÑÏàò X)</text>
            <span className="with">
              <button
                onClick={() => handleWithSelect(1)}
                className={withSelected === 1 ? "selected" : ""}
              >
                Î∂ÄÎ™®Îãò
              </button>
              <button
                onClick={() => handleWithSelect(2)}
                className={withSelected === 2 ? "selected" : ""}
              >
                ÏïÑ Ïù¥
              </button>
              <button
                onClick={() => handleWithSelect(3)}
                className={withSelected === 3 ? "selected" : ""}
              >
                Ïó∞ Ïù∏
              </button>
              <button
                onClick={() => handleWithSelect(4)}
                className={withSelected === 4 ? "selected" : ""}
              >
                Ïπú Íµ¨
              </button>
            </span>
          </div>
          <div>
            <button
              id="result"
              onClick={() => {
                navigate("options");
              }}
            >
              Í≤ΩÎ°ú Ï∂îÏ≤ú Î∞õÍ∏∞
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CheckInfo;
