import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../CheckInfo.css";
import "../fonts/fonts.css";

function CheckInfo() {
  const navigate = useNavigate();

  // 선택된 정보 저장
  //const [getInfo, setGetInfo] = useState("");

  // 차 유무 선택
  const [carSelected, setCarSelected] = useState(null);

  const handleCarSelect = (index) => {
    setCarSelected(index === carSelected ? carSelected : index);
  };
  console.log(carSelected);

  // 여행지역 선택
  const [locationSelected, setLocationSelected] = useState(null);

  const handleLocationSelect = (index) => {
    setLocationSelected(index === locationSelected ? null : index);
  };

  // 동행자 유형 선택
  const [withSelected, setWithSelected] = useState(null);

  const handleWithSelect = (index) => {
    setWithSelected(index === withSelected ? null : index);
  };

  const buttons = document.querySelectorAll(".theme button"); // 모든 버튼 선택
  const minSelect = 1; // 최소 선택 가능한 개수
  const maxSelect = 3; // 최대 선택 가능한 개수
  let selectedCount = 0; // 선택된 버튼 개수 초기화

  // 각 버튼에 클릭 이벤트 추가
  buttons.forEach(button => {
    button.addEventListener('click', () => {
      // 선택된 버튼의 개수가 최대 개수를 초과하면 선택 불가능
      if (
        selectedCount >= maxSelect &&
        !button.classList.contains("selected")
      ) {
        alert(`최대 ${maxSelect}개까지만 선택할 수 있습니다.`);
        return;
      }
      // 선택되어 있지 않은 버튼을 선택하면 개수 증가
      if (!button.classList.contains("selected")) {
        selectedCount++;
        button.classList.add("selected"); // 선택된 버튼에 selected 클래스 추가
      }
      // 선택되어 있는 버튼을 다시 선택하면 개수 감소
      else {
        selectedCount--;
        button.classList.remove("selected"); // 선택 취소된 버튼의 selected 클래스 제거
      }
      // 선택된 버튼의 개수가 최소 개수 미만이면 선택 불가능
      // if (selectedCount < minSelect) {
      //   alert(`최소 ${minSelect}개 이상 선택해야 합니다.`);
      //   return;
      // }
    });
  });

  return (
    <div className="CheckInfo">
      <div id="title">
        <text>예정된 여행에 대한 정보를 선택해주세요.</text>
      </div>
      <div className="Q">
        <text>1. 여행일자를 선택해주세요.</text>
        <button id="day">날짜 선택 🗓️</button>
      </div>
      <div className="Q">
        <text>2. 여행 시작과 종료 시간을 선택해주세요.</text>
        <div className="CheckTime">
          <text>시작</text>
          <select>
            <option value={"아침(8-9시)"}>아침 (8-9시)</option>
            <option value={"점심(12-13시)"}>점심 (12-13시)</option>
            <option value={"오후(14-15시)"}>오후 (14-15시)</option>
          </select>
          <text>|</text>
          <text>종료</text>
          <select>
            <option value={"18-20시"}>18-20시</option>
            <option value={"20-21시"}>20-21시</option>
            <option value={"21-22시"}>21-22시</option>
          </select>
        </div>
        <div className="Q">
          <text>3. 이동수단으로 차를 이용하실 건가요?</text>
          <span className="car">
            <button
              className={carSelected === 1 ? "selected" : ""}
              onClick={() => handleCarSelect(1)}
            >
              O
            </button>
            <button
              className={carSelected === 2 ? "selected" : ""}
              onClick={() => handleCarSelect(2)}
            >
              X
            </button>
          </span>
          <div className="Q">
            <text>4. 여행 테마를 선택해주세요. (최대 3개 선택 가능)</text>
            <span className="theme">
              <button>힐링</button>
              <button>자연</button>
              <button>체험</button>
              <br />
              <button>관광</button>
              <button>맛집</button>
              <button>카페</button>
            </span>
          </div>
          <div className="Q">
            <text>5. 선호하는 여행 지역을 선택해주세요. (필수 X)</text>
            <span className="location">
              <button
                className={locationSelected === 1 ? "selected" : ""}
                onClick={() => handleLocationSelect(1)}
              >
                동 부
              </button>
              <button
                className={locationSelected === 2 ? "selected" : ""}
                onClick={() => handleLocationSelect(2)}
              >
                서 부
              </button>
            </span>
          </div>
          <div className="Q">
            <text>6. 동행자 유형을 선택해주세요. (필수 X)</text>
            <span className="with">
              <button
                onClick={() => handleWithSelect(1)}
                className={withSelected === 1 ? "selected" : ""}
              >
                부모님
              </button>
              <button
                onClick={() => handleWithSelect(2)}
                className={withSelected === 2 ? "selected" : ""}
              >
                아 이
              </button>
              <button
                onClick={() => handleWithSelect(3)}
                className={withSelected === 3 ? "selected" : ""}
              >
                연 인
              </button>
              <button
                onClick={() => handleWithSelect(4)}
                className={withSelected === 4 ? "selected" : ""}
              >
                친 구
              </button>
            </span>
          </div>
          <div>
            <button
              id="result"
              onClick={() => {
                navigate("options");
              }}
            >
              경로 추천 받기
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CheckInfo;
