import { useNavigate, useLocation } from "react-router-dom";
import WordCloud from "react-d3-cloud";
import "../DetailRoute.css";
import KakaoMap from "../components/KakaoMap";
import DayBtn from "../components/DayBtn";
import Route from "../components/Route";

function DetailRoute() {
  const navigate = useNavigate();
  const location = useLocation();
  const duration = location.state.duration;
  const selectedData = location.state.selectedData;
  //지울 것
  console.log(duration, selectedData);

  let durationStr;
  if (duration === 1) {
    durationStr = "당일치기";
  } else if (duration === 2) {
    durationStr = "1박 2일";
  } else if (duration === 3) {
    durationStr = "2박 3일";
  }

  const data = [
    { text: selectedData.startDate, value: 50, fill: "black" },
    { test: durationStr, value: 400 },
    { text: selectedData.location, value: 300 },
    { text: selectedData.with, value: 600 },
    ...selectedData.themes.map((theme) => ({ text: theme, value: 2500 })),
  ];

  return (
    <div className="DetailRoute">
      <div className="about">
        <button id="back" onClick={() => navigate(-1)}>
          {`<`}
        </button>
        <text>자세한 경로는 ... </text>
      </div>
      <div className="info">
        <div id="left">
          <div id="lTop">
            <DayBtn duration={duration} />
          </div>
          <div id="lMiddle">
            <text># 키워드</text>
            <div className="wordcloud">
              <WordCloud
                data={data}
                width={200}
                height={120}
                padding={3}
                fontSize={(word) => Math.log2(word.value) * 2}
                rotate={(word) => Math.random() * 90 - 45}
                font="sunflowerL"
                fill={"darkgray"}
              />
            </div>
          </div>
          <div id="lDown">
            <Route />
          </div>
        </div>

        <div id="right">
          <KakaoMap />
        </div>
      </div>
    </div>
  );
}

export default DetailRoute;
