import React, { useEffect } from "react";

const { kakao } = window;

function KakaoMap(props) {
  useEffect(() => {
    const container = document.getElementById("map");

    if (props.responseData && Array.isArray(props.responseData)) {
      const markerPositions = props.responseData.map((item) => {
        const latitude = item.latitude;
        const longitude = item.longitude;
        return new kakao.maps.LatLng(latitude, longitude);
      });

      const bounds = new kakao.maps.LatLngBounds();
      markerPositions.forEach((position) => {
        bounds.extend(position);
      });

      const center = getCenter(bounds);

      const options = {
        center: center,
        level: 10,
      };

      const map = new kakao.maps.Map(container, options);

      props.responseData.forEach((place, index) => {
        const latitude = place.latitude;
        const longitude = place.longitude;

        const marker = new kakao.maps.Marker({
          position: new kakao.maps.LatLng(latitude, longitude),
        });

        marker.setMap(map);

        // 마커에 마우스 이벤트 리스너 추가
        kakao.maps.event.addListener(marker, 'mouseover', function() {
          openOverlay(map, marker, place.title);
        });
        kakao.maps.event.addListener(marker, 'mouseout', function() {
          closeOverlay();
        });
      });
    }
  }, [props.responseData]);

  function getCenter(bounds) {
    const ne = bounds.getNorthEast();
    const sw = bounds.getSouthWest();
    const centerLat = (ne.getLat() + sw.getLat()) / 2;
    const centerLng = (ne.getLng() + sw.getLng()) / 2;
    return new kakao.maps.LatLng(centerLat, centerLng);
  }

  // 오버레이 열기
  function openOverlay(map, marker, title) {
    closeOverlay(); // 기존 오버레이가 열려있으면 닫습니다.

    const overlay = new kakao.maps.CustomOverlay({
      content: `<div class="overlay">${title}</div>`,
      position: marker.getPosition(),
      yAnchor: 1,
      zIndex: 1,
    });

    overlay.setMap(map);
  }

  // 오버레이 닫기
  function closeOverlay() {
    const overlays = kakao.maps.CustomOverlay.getAll();
    overlays.forEach((overlay) => {
      overlay.setMap(null);
    });
  }

  return <div id="map"></div>;
}

export default KakaoMap
