import { useState } from "react";
import Route from "./Route";
function DayBtn(props) {
  const [selectedDay, setSelectedDay] = useState(1);

  const handleDayClick = (day) => {
    setSelectedDay(day);
  };

  return (
    <div className="DayBtn">
      {props.duration === 1 ? (
        <button id="one" onClick={() => handleDayClick(1)}>
          Day 1
        </button>
      ) : props.duration === 2 ? (
        <>
          <button id="two-1" onClick={() => handleDayClick(1)}>
            Day 1
          </button>
          |
          <button id="two-2" onClick={() => handleDayClick(2)}>
            Day 2
          </button>
        </>
      ) : (
        <>
          <button id="three-1" onClick={() => handleDayClick(1)}>
            Day 1
          </button>
          |
          <button id="three-2" onClick={() => handleDayClick(2)}>
            Day 2
          </button>
          |
          <button id="three-3" onClick={() => handleDayClick(3)}>
            Day 3
          </button>
        </>
      )}
      <Route selectedDay={selectedDay} />
    </div>
  );
}

export default DayBtn;
